import * as types from './constants';

const INITIAL_STATE = {
  api: {
    isLoading: false,
    errors: null,
  },
  users: [],
};

export default function userReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case types.USER_READ:
      return Object.assign({}, state, {
        api: {
          isLoading: true,
          errors: null,
        },
      });
    case types.USER_READ_SUCCEEDED:
      return Object.assign({}, state, {
        api: {
          isLoading: false,
          errors: null,
        },
        users: [
          ...state.users?.filter(record => record.id !== action.response.data.id),
          action.response.data,
        ],
      });
    case types.USER_READ_FAILED:
      return Object.assign({}, state, {
        api: {
          isLoading: false,
          errors: action.response.message,
        },
      });
    case types.USER_LIST:
      return Object.assign({}, state, {
        api: {
          isLoading: true,
        },
      });
    case types.USER_LIST_SUCCEEDED:
      return Object.assign({}, state, {
        api: {
          isLoading: false,
        },
        users: action.response.data,
      });
    case types.USER_LIST_FAILED:
      return Object.assign({}, state, {
        api: {
          isLoading: false,
          errors: action.response.message,
        },
      });
    case types.USER_UPDATE:
      return Object.assign({}, state, {
        api: {
          isLoading: true,
        },
      });
    case types.USER_UPDATE_SUCCEEDED:
      return Object.assign({}, state, {
        api: {
          isLoading: false,
        },
        users: [
          ...state.users.filter(record => record.id !== action.response.data.id),
          action.response.data,
        ],
      });
    case types.USER_UPDATE_FAILED:
      return Object.assign({}, state, {
        api: {
          isLoading: false,
          errors: action.response.message,
        },
      });
    default:
      return state;
  }
}

store/sagas.js.code:
import { put, call, all, spawn, takeEvery } from 'redux-saga/effects';
import service from "./services"
import * as types from './constants';
import * as actions from './actions';

// Get User
function* userReadWorker(action) {
  try {
    const result = yield call(service.user_read, action);
    yield put(actions.user_read_succeeded(result, action));
  } catch (err) {
    yield put(actions.user_read_failed(err, action));
  }
}

function* userReadWatcher() {
  yield takeEvery(types.USER_READ, userReadWorker)
}

// Get Users
function* userListWorker(action) {
  try {
    const result = yield call(service.user_list, action);
    yield put(actions.user_list_succeeded(result, action));
  } catch (err) {
    yield put(actions.user_list_failed(err, action));
  }
}

function* userListWatcher() {
  yield takeEvery(types.USER_LIST, userListWorker)
}

// Update User
function* userUpdateWorker(action) {
  try {
    const result = yield call(service.user_update, action);
    yield put(actions.user_update_succeeded(result, action));
  } catch (err) {
    yield put(actions.user_update_failed(err, action));
  }
}

function* userUpdateWatcher() {
  yield takeEvery(types.USER_UPDATE, userUpdateWorker)
}

export default function* authRootSaga() {
  const sagas = [
    userReadWatcher,
    userReadWatcher,
    userListWatcher,
    userUpdateWatcher
  ];
  yield all(
    sagas.map(saga =>
      spawn(function*() {
        while (true) {
          try {
            yield call(saga);
            break;
          } catch (e) {
            console.log(e);
          }
        }
      }),
    ),
  );
}

