          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(JSON.stringify(state));
  return {
    token: state.login.token,
    api: state.login.api,
    user: state.login.user,
  };
