  useEffect(() => {
    if (prevProps?.api.isLoading) {
      if (api.error?.type === API_PASSWORD_RESET_FAILED) {
        let message =
          api.error?.code === 400
            ? "This email is not registered.\nPlease signup"
            : api.error.message
        Alert.alert("Error", message)
      }

      if (api.success) {
        Alert.alert(
          "Password Reset",
          "Password reset link has been sent to your email address"
        )
        navigation.goBack()
      }
    }
  }, [api])

  const handlePasswordReset = () => {
    if (!validateEmail.test(email)) {
      Alert.alert("Error", "Please enter a valid email address.")
      return
    }
    props.reset(email)
  }

  const handleEmail = email => {
    setEmail(email)
  }

  return (
    <View style={{ flex: 1 }}>
      <KeyboardAwareScrollView contentContainerStyle={styles.screen}>
        <Image
          style={[styles.image, { width: 365, height: 161 }]}
          source={{
            uri: LOGO_URL
          }}
        />
        <Text style={styles.heading}>{"Password Recovery"}</Text>
        <View style={styles.fieldContainer}>
          <Text style={styles.label}>Email Address</Text>
          <TextInput
            value={email}
            onChangeText={email => handleEmail(email)}
            placeholder="eg: yourname@gmail.com"
            size="small"
            style={styles.input}
            keyboardType="email-address"
            textStyle={styles.text}
            autoCapitalize="none"
          />
        </View>
        <TouchableOpacity
          disabled={props.api.isLoading}
          activeOpacity={0.7}
          style={[styles.actionButon]}
          onPress={handlePasswordReset}
        >
          <Text
            style={{
              color: "#fff",
              fontSize: 15
            }}
          >
            {"Reset Password"}
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          activeOpacity={0.7}
          onPress={() => {
            navigation.goBack()
          }}
        >
          <Text style={[styles.textRow]}>Back to login?</Text>
        </TouchableOpacity>
      </KeyboardAwareScrollView>
    </View>
  )
}

function mapStateToProps(state) {
  return {
    api: state.login.api
  }
}

const mapDispatchToProps = dispatch => {
  return {
    reset: email => dispatch(apiPasswordResetRequest({ email }))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(PasswordRecover)

