    login: (email, password) =>
      dispatch(apiLoginRequest({ username: email, password })),
    signup: (email, password) => dispatch(apiSignupRequest({ email, password })),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(LoginSignup);

